package com.luckydeal.share;import android.net.Uri;import com.facebook.share.model.ShareLinkContent;import com.facebook.share.widget.ShareDialog;import com.luckyapp.common.utils.LogUtil;import com.luckydeal.R;/** * Created by zyy * on 2019/5/9 */public class FacebookShare {    private static final String TAG = "FacebookShare";    public boolean shareImage(ShareDialog shareDialog, String imageUrl, String url, String title, int imgType) {        Uri imageUri;        if (ShareDownloadHelper.INVITE == imgType) {            imageUri = ShareDownloadHelper.getInstance().getBitmapFromDrawable(R.mipmap.ic_launcher);        } else if (ShareDownloadHelper.DETAIL == imgType) {            imageUri = ShareDownloadHelper.getInstance().getBitmapFromDrawable(R.mipmap.ic_launcher);        } else {            imageUri = ShareDownloadHelper.getInstance().getBitmapFromDrawable(R.mipmap.ic_launcher);        }        LogUtil.d(TAG,"data=FacebookShare"+url);        ShareLinkContent linkContent = new ShareLinkContent.Builder()                .setContentTitle(title)                .setContentUrl(Uri.parse(url))                .setImageUrl(imageUri)                .build();        shareDialog.show(linkContent);        return true;    }}