package com.luckydeal.share;import android.app.Activity;import android.net.Uri;import android.util.Log;import com.facebook.share.model.ShareLinkContent;import com.facebook.share.widget.MessageDialog;import com.luckyapp.common.utils.ToastUtil;import com.luckydeal.R;import com.luckydeal.util.AppUtil;/** * Created by zyy * on 2019/5/9 */public class MessengerShare {    public static final String MESSENGER_PACKAGE_NAME = "com.facebook.orca";    public static final String TAG = "messenger";    public boolean shareImg(MessageDialog messageDialog, Activity context, String title, String url, String content, String imageUrl) {        if (!AppUtil.isAppInstalled(MESSENGER_PACKAGE_NAME)) {            ToastUtil.show(R.string.install_facebook_messenger);            Log.e(TAG, "messenger 没有安装");            return false;        }        ShareLinkContent.Builder shareLinkContentBuilder = new ShareLinkContent.Builder()                .setContentTitle(title)                .setContentDescription(content)                .setImageUrl(Uri.parse(imageUrl))                .setContentUrl(Uri.parse(url));        messageDialog.show(shareLinkContentBuilder.build());        return true;    }}