package com.luckydeal.share;import android.content.Context;import android.net.Uri;import com.luckyapp.common.GlobalApp;import com.luckydeal.R;import com.twitter.sdk.android.tweetcomposer.TweetComposer;import java.net.MalformedURLException;import java.net.URL;/** * Created by zyy * on 2019/5/9 */public class TwitterShare {    private static final String TWITTER_PACKAGE_NAME = "com.twitter.android";    public boolean shareImg(Context context, String title, String url, String content, String imageUrl, int imgType) {        try {            TweetComposer.Builder builder = null;            if (ShareDownloadHelper.INVITE == imgType) {                builder = new TweetComposer.Builder(context).text(title).image(Uri.parse("android.resource://" + GlobalApp.get().getPackageName() + "/" + R.mipmap.ic_launcher))                        .url(new URL(url));            } else if (ShareDownloadHelper.DETAIL == imgType) {                builder = new TweetComposer.Builder(context).text(title).image(Uri.parse("android.resource://" + GlobalApp.get().getPackageName() + "/" + R.mipmap.ic_launcher))                        .url(new URL(url));            } else {                builder = new TweetComposer.Builder(context).text(title).image(Uri.parse("android.resource://" + GlobalApp.get().getPackageName() + "/" + R.mipmap.ic_launcher))                        .url(new URL(url));            }            builder.show();            return true;        } catch (MalformedURLException e) {            e.printStackTrace();        }        return false;    }}