package com.luckyapp.common.utils;/** * Created by zyy * on 2019/5/14 */public class Utils {    private static long lastClickTime;    /**     * 防止多次重复快速调用     *     * @return     */    public synchronized static boolean isFastClick(long delayTime) {        long time = System.currentTimeMillis();        long result = time - lastClickTime;        if (result < delayTime && result > 0) {            return true;        }        lastClickTime = time;        return false;    }}